import React, { useState } from "react";
import { Drawer, List, ListItem, ListItemIcon, ListItemText, IconButton } from "@material-ui/core";
import { Home as HomeIcon, Person as PersonIcon, Mail as MailIcon, Menu as MenuIcon } from "@material-ui/icons";
import "./sidebar.css";

const Sidebar = () => {
  const [drawerOpen, setDrawerOpen] = useState(false);

  const handleDrawerToggle = () => {
    setDrawerOpen(!drawerOpen);
  };

  return (
    <div className="sidebar">
      <IconButton className="sidebar_icon" onClick={handleDrawerToggle}>
        {drawerOpen ? <MenuIcon /> : <MenuIcon />}
      </IconButton>
      <Drawer anchor="left" open={drawerOpen} onClose={handleDrawerToggle}>
        <List>
          <ListItem button>
            <ListItemIcon>
              <HomeIcon />
            </ListItemIcon>
            <ListItemText primary="" /> {/* Empty text to hide it */}
          </ListItem>
          <ListItem button>
            <ListItemIcon>
              <PersonIcon />
            </ListItemIcon>
            <ListItemText primary="" /> {/* Empty text to hide it */}
          </ListItem>
          <ListItem button>
            <ListItemIcon>
              <MailIcon />
            </ListItemIcon>
            <ListItemText primary="" /> {/* Empty text to hide it */}
          </ListItem>
        </List>
      </Drawer>
    </div>
  );
};

export default Sidebar;
