import React, { useState } from "react";
import { IconButton } from "@material-ui/core";
import {
  Home as HomeIcon,
  Person as PersonIcon,
  Mail as MailIcon,
  Menu as MenuIcon
} from "@material-ui/icons";
import "./sidebar.css";

const Sidebar = ({ handleDrawerToggle }) => {
  const [expanded, setExpanded] = useState(false);

  const handleSidebarToggle = () => {
    setExpanded(!expanded);
  };

  return (
    <div className={`sidebar ${expanded ? "expanded" : ""}`}>
      <div className="sidebar-icons">
        <IconButton className="sidebar-icon" onClick={handleSidebarToggle}>
          <HomeIcon />
        </IconButton>
        <span className={`sidebar-label ${expanded ? "visible" : ""}`}>Home</span>
        <IconButton className="sidebar-icon" onClick={handleSidebarToggle}>
          <PersonIcon />
        </IconButton>
        <span className={`sidebar-label ${expanded ? "visible" : ""}`}>About</span>
        <IconButton className="sidebar-icon" onClick={handleSidebarToggle}>
          <MailIcon />
        </IconButton>
        <span className={`sidebar-label ${expanded ? "visible" : ""}`}>Contact</span>
      </div>
      <IconButton className="navbar-icon" onClick={handleDrawerToggle}>
        <MenuIcon style={{ color: "#fff" }} />
      </IconButton>
    </div>
  );
};

export default Sidebar;
